using UnityEngine.UI;

public class ButtonOpenPlatform : MonoBehaviour
{
    [Header("171177117117771171177117117777171177117117771171177117117777171177117117770171177117117772171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777")]
    [FieldLabel("171177117117771171177117117777171177117117771171177117117777171177117117770171177117117772171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777")] public Button[] buttons; // 

    [Header("RawImage171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777")]
    [FieldLabel("RawImage171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777")] public RawImage[] rawImages; // 

    [Header("")]
    [FieldLabel("171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117770171177117117778171177117117770171177117117770171177117117771171177117117777171177117117771171177117117777171177117117770171177117117772")] public Color normalColor = Color.white; // 
    [FieldLabel("171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117770171177117117778171177117117770171177117117770171177117117771171177117117777171177117117771171177117117777171177117117770171177117117772")] public Color pressedColor = Color.gray; // 
    [FieldLabel("171177117117771171177117117777171177117117771171177117117777171177117117770171177117117775171177117117770171177117117778171177117117770171177117117770171177117117771171177117117777171177117117771171177117117777171177117117770171177117117772")] public Color hoverColor = new Color(0.8f, 0.8f, 0.8f, 1f); // 
    [FieldLabel("171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117770171177117117778171177117117770171177117117770171177117117771171177117117777171177117117771171177117117777171177117117770171177117117772")] public Color disabledColor = Color.gray; // 

    // 
    private int currentSelectedIndex = -1;

    // 
    private ColorBlock[] originalColorBlocks;

    private void Start()
    {
        rawImages[0].gameObject.SetActive(true); // 
        InitializeButtons();
        InitializeRawImages();
    }

    // 171177117117771171177117117777171177117117771171177117117777171177117117770171177117117773171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117770171177117117772
    private void InitializeButtons()
    {
        if (buttons == null || buttons.Length == 0)
        {
            Debug.LogWarning("ButtonOpenPlatform: ");
            return;
        }

        // 
        originalColorBlocks = new ColorBlock[buttons.Length];

        for (int i = 0; i < buttons.Length; i++)
        {
            if (buttons[i] != null)
            {
                // 
                originalColorBlocks[i] = buttons[i].colors;

                // 
                SetButtonColors(i, false);

                // 
                int buttonIndex = i; // 
                buttons[i].onClick.AddListener(() => OnButtonClicked(buttonIndex));
            }
        }
    }

    // 171177117117771171177117117777171177117117771171177117117777171177117117770171177117117773171177117117771171177117117777171177117117771171177117117777RawImage
    private void InitializeRawImages()
    {
        if (rawImages == null || rawImages.Length == 0)
        {
            Debug.LogWarning("ButtonOpenPlatform: ");
            return;
        }
        // 
        for (int i = 0; i < rawImages.Length; i++)
        {
            if (rawImages[i] != null)
            {
                rawImages[i].gameObject.SetActive(false);
            }
        }
    }

    // 
    private void OnButtonClicked(int buttonIndex)
    {
        if (buttonIndex < 0 || buttonIndex >= buttons.Length || buttons[buttonIndex] == null)
            return;

        // 
        if (currentSelectedIndex == buttonIndex)
        {
            Debug.Log("171177117117771171177117117777171177117117771171177117117777171177117117770171177117117772 " + buttonIndex + " ");
            return;
        }

        // 
        if (currentSelectedIndex >= 0 && currentSelectedIndex < buttons.Length)
        {
            HideRawImage(currentSelectedIndex);
            SetButtonColors(currentSelectedIndex, false);
        }

        // 
        ShowRawImage(buttonIndex);
        SetButtonColors(buttonIndex, true);

        // 
        currentSelectedIndex = buttonIndex;

        // 
        OnButtonSelected(buttonIndex);

        Debug.Log("171177117117771171177117117777171177117117771171177117117777171177117117770171177117117772 " + buttonIndex + " ");
    }

    // 
    private void ShowRawImage(int index)
    {
        if (rawImages != null && index >= 0 && index < rawImages.Length && rawImages[index] != null)
        {
            rawImages[index].gameObject.SetActive(true);
        }
    }

    // 
    private void HideRawImage(int index)
    {
        if (rawImages != null && index >= 0 && index < rawImages.Length && rawImages[index] != null)
        {
            rawImages[index].gameObject.SetActive(false);
        }
    }

    // 171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777RawImage
    private void HideAllRawImages()
    {
        if (rawImages != null)
        {
            for (int i = 0; i < rawImages.Length; i++)
            {
                HideRawImage(i);
            }
        }
    }

    // 
    private void SetButtonColors(int buttonIndex, bool isSelected)
    {
        if (buttonIndex < 0 || buttonIndex >= buttons.Length || buttons[buttonIndex] == null)
            return;

        ColorBlock colorBlock = buttons[buttonIndex].colors;

        if (isSelected)
        {
            // 
            colorBlock.normalColor = pressedColor;
            colorBlock.highlightedColor = pressedColor;
            colorBlock.pressedColor = pressedColor;
            colorBlock.selectedColor = pressedColor;
        }
        else
        {
            // 
            colorBlock.normalColor = normalColor;
            colorBlock.highlightedColor = hoverColor;
            colorBlock.pressedColor = pressedColor;
            colorBlock.selectedColor = normalColor;
        }

        colorBlock.disabledColor = disabledColor;
        colorBlock.colorMultiplier = 1f;
        colorBlock.fadeDuration = 0.1f;

        buttons[buttonIndex].colors = colorBlock;
    }

    // 
    protected virtual void OnButtonSelected(int buttonIndex)
    {
        // 
        // 

        switch (buttonIndex)
        {
            case 0:
                Debug.Log("");
                break;
            case 1:
                Debug.Log("");
                break;
            case 2:
                Debug.Log("");
                break;
            default:
                Debug.Log("171177117117770171177117117775171177117117771171177117117777171177117117771171177117117777171177117117771171177117117777171177117117770171177117117775171177117117771171177117117777 " + (buttonIndex + 1) + "  " + (buttonIndex + 1) + " 171177117117771171177117117777171177117117771171177117117777RawImage");
                break;
        }
    }

    // 
    public void SelectButton(int buttonIndex)
    {
        OnButtonClicked(buttonIndex);
    }

    // 
    public int GetSelectedButtonIndex()
    {
        return currentSelectedIndex;
    }

    // 
    public void ClearSelection()
    {
        if (currentSelectedIndex >= 0 && currentSelectedIndex < buttons.Length)
        {
            SetButtonColors(currentSelectedIndex, false);
            HideRawImage(currentSelectedIndex);
        }
        currentSelectedIndex = -1;
    }

    // 
    public void SetButtonInteractable(int buttonIndex, bool interactable)
    {
        if (buttonIndex >= 0 && buttonIndex < buttons.Length && buttons[buttonIndex] != null)
        {
            buttons[buttonIndex].interactable = interactable;
        }
    }

    // 
    public RawImage GetRawImage(int buttonIndex)
    {
        if (rawImages != null && buttonIndex >= 0 && buttonIndex < rawImages.Length)
        {
            return rawImages[buttonIndex];
        }
        return null;
    }

    // 
    public Button GetButton(int buttonIndex)
    {
        if (buttons != null && buttonIndex >= 0 && buttonIndex < buttons.Length)
        {
            return buttons[buttonIndex];
        }
        return null;
    }
}
